CREATE TEMPORARY TABLE "SubProgramInfo"
  ( id          SERIAL PRIMARY KEY
  , subprogram  int4
  , info        text NOT NULL DEFAULT ''
  );

-- Production data snapshot (April 9, 2014)

-- Old programs match new subprograms
UPDATE "SubProgram" sp SET help = spi.info
FROM "SubProgramInfo" spi
WHERE spi.subprogram = sp.id;

